import {
    UPDATE_ASSETS,
    CHANGE_SORT_VALUE,
    CHANGE_FILTER_TYPE,
    CHANGE_FILTER_VALUE
} from 'actionTypes';

export const setAssets = (assets) => dispatch => {
    dispatch({
        type: UPDATE_ASSETS,
        payload: assets
    })
};
export const setSortValue = (value) => dispatch => {
    dispatch({
        type: CHANGE_SORT_VALUE,
        payload: value
    })
};
export const setFilterType = (filterType) => dispatch => {
    dispatch({
        type: CHANGE_FILTER_TYPE,
        payload: filterType
    })
};
export const setFilterValue = (filterValue) => dispatch => {
    dispatch({
        type: CHANGE_FILTER_VALUE,
        payload: filterValue.toLowerCase()
    })
};