import React from 'react';
import './assets.css';
import _ from 'lodash';

const Assets = ({items}) => {
    return (
        <div className='data-wrapper'>
            {items.length ?
            <table>
                    <tbody>
                    <tr>
                        <td>ID</td>
                        <td>NAME</td>
                        <td>PRICE</td>
                        <td>LAST UPDATE</td>
                        <td>TYPE</td>
                    </tr>
                    {items.map((item) =>
                        <tr key={_.uniqueId()}>
                            <td>{item.id}</td>
                            <td>{item.assetName}</td>
                            <td>{item.price}</td>
                            <td>{item.lastUpdate}</td>
                            <td>{item.type}</td>
                        </tr>
                    )}
                    </tbody>
            </table> : 'NOTHING TO SHOW'}
        </div>
    )
};

export default Assets;