import React from 'react';
import './select.css';

const Select = ({sortByProperty, setFilterType}) => {
    const selectChanging = (event) => {
        let value = event.target.value;

        sortByProperty ? sortByProperty(value) : setFilterType(value)
    };
    return (
        <select onChange={selectChanging}>
            <option>id</option>
            <option>assetName</option>
            <option>price</option>
            <option>lastUpdate</option>
            <option>type</option>
        </select>
    )
};
export default Select;